import { createRouter, createWebHistory } from "vue-router";
import HomeView from "@/views/HomeView.vue"; // Ganti dengan path sesuai struktur proyek Anda
import AdminArticlesView from "@/views/AdminArticlesView.vue"; // Ganti dengan path sesuai struktur proyek Anda
import UsersView from "@/views/UsersView.vue"; // Ganti dengan path sesuai struktur proyek Anda
import ArticlesView from "@/views/ArticlesView.vue"; // Ganti dengan path sesuai struktur proyek Anda
import ProfileView from "@/views/ProfileView.vue"; // Ganti dengan path sesuai struktur proyek Anda
import { useAuthStore } from "@/stores/auth"; // Ganti dengan path sesuai struktur proyek Anda

const routes = [
  {
    path: "/",
    redirect: "/home", // Redirect ke halaman home atau halaman lainnya yang sesuai
  },
  {
    path: "/home",
    name: "Home",
    component: HomeView,
  },
  {
    path: "/admin-articles",
    name: "AdminArticles",
    component: AdminArticlesView,
    meta: { requiresAdmin: true }, // Middleware untuk admin
  },
  {
    path: "/users",
    name: "Users",
    component: UsersView,
    meta: { requiresAdmin: true }, // Middleware untuk admin
  },
  {
    path: "/articles",
    name: "Articles",
    component: ArticlesView,
    meta: { requiresDemo1: true }, // Middleware untuk demo1
  },
  {
    path: "/profile",
    name: "Profile",
    component: ProfileView,
    meta: { requiresDemo1: true }, // Middleware untuk demo1
  },
  // Tambahan: Handle route 404 jika diperlukan
  {
    path: "/:catchAll(.*)",
    redirect: "/home", // Redirect ke halaman home jika route tidak ditemukan
  },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

router.beforeEach((to, from, next) => {
  const authStore = useAuthStore();
  const isAuthenticated = authStore.isAuthenticated;
  const isAdmin = authStore.username === "admin";
  const isDemo1 = authStore.username === "demo1";

  if (to.meta.requiresAdmin && (!isAuthenticated || !isAdmin)) {
    next("/home"); // Redirect jika tidak memenuhi syarat admin
  } else if (to.meta.requiresDemo1 && (!isAuthenticated || !isDemo1)) {
    next("/articles"); // Redirect jika tidak memenuhi syarat demo1
  } else {
    next(); // Lanjutkan navigasi
  }
});

export default router;
